import React from 'react'
import { Card, CardCategory, StyledCard } from './Styled'
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Pagination } from "swiper";


function Cards() {
  return (
    <Swiper
      slidesPerView={4}
      spaceBetween={1}
      loop={true}
      pagination={{
        clickable: true,
      }}
      modules={[Pagination]}
      className="slider"
    >
      <SwiperSlide>
        <StyledCard>
          <CardCategory>POP</CardCategory>
        </StyledCard>
      </SwiperSlide>
      <SwiperSlide>
        <Card>
          <CardCategory>INDIE</CardCategory>
        </Card>
      </SwiperSlide>
      <SwiperSlide>
        <StyledCard>
          <CardCategory>RAP</CardCategory>
        </StyledCard>
      </SwiperSlide>
      <SwiperSlide>
        <Card>
          <CardCategory>RETRO</CardCategory>
        </Card>
      </SwiperSlide>
      <SwiperSlide>
        <Card>
          <CardCategory>ROCK</CardCategory>
        </Card>
      </SwiperSlide>
    </Swiper>
  );
}

export default Cards
