import React from 'react'
import { Card, CardCategory, StyledCard } from './Styled'
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Pagination } from "swiper";


function Cards() {
  return (
    <Swiper
      slidesPerView={3}
      spaceBetween={10}
      pagination={{
        clickable: true,
      }}
      modules={[Pagination]}
    >
      <SwiperSlide>
        <StyledCard>
          <CardCategory>POP</CardCategory>
        </StyledCard>
      </SwiperSlide>
      <SwiperSlide>
        <Card>
          <CardCategory>INDIE</CardCategory>
        </Card>
      </SwiperSlide>
      <SwiperSlide>
        <StyledCard>
          <CardCategory>RAP</CardCategory>
        </StyledCard>
      </SwiperSlide>
      <SwiperSlide>
        <Card>
          <CardCategory>RETRO</CardCategory>
        </Card>
      </SwiperSlide>
    </Swiper>
  );
}

export default Cards
