import styled from "styled-components";

export const Container = styled.div`
max-width: 100%;
width: 1200px;
margin-left:170px;`;

const FlexWrapper = styled.div`
  display: flex;
  gap: 20px;
  align-items: center;
  margin-left: ${(props) => props.marginLeft || "0px"};
`;

const Sidebar = styled.ul`
  text-align: left;
  width: 150px;
  font-family: "Mukta", sans-serif;
  margin: 0;
  position: absolute;
  left: 0;
  z-index: 1;
`;

const Options = styled.li`
  color: white;
  list-style-type: none;
  font-family: "Mukta", sans-serif;
  font-size: 16px;
  position: ${(props => props.position || "block")};
  z-index: 9;
  background: ${(props => props.background) || "none"};
`;

const Title = styled.h3`
  font-size: 20px;
  color: palevioletred;
  margin: 0;
  text-align: left;
  padding: ${(props) => props.padding || "0px"};
  background-color: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(5px);
`;

const Navbar = styled.div`
  border-bottom: 1px solid white;
  display: flex;
  justify-content: space-between;
  padding: 10px 20px;
`;

const User = styled.p`
  font-size: 18px;
  color: palevioletred;
  margin: 0;
`;

const Card = styled.section`
  margin-top: 20px;
  position: relative;
  border-radius: 6px;
  height: 150px;
  width: 250px;
  background: rgb(2, 0, 36);
  background: linear-gradient(
    20deg,
    rgba(2, 0, 36, 1) 0%,
    rgb(213 23 76 / 84%) 18%,
    rgb(0 212 255 / 32%) 100%
  );
`;

const StyledCard = styled(Card)`
    background: rgb(8, 0, 13);
    background: linear-gradient(
      20deg,
      rgba(8, 0, 13, 1) 0%,
      #673ab7 50%,
      rgb(235 217 217) 100%
    );
`;
const CardCategory = styled.h3`
  background-color: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(5px);
  color: white;
  position: absolute;
  bottom: 0;
  width: 250px;
  height: 50px;
  text-align: left;
  margin: 0;
  padding: 0px 10px;
  display: flex;
  flex-direction: column;
  opacity: 0.85;
`;

export const Album = styled.section`
width:300px;
height:200px;
`;

export {
  FlexWrapper,
  Sidebar,
  Options,
  Navbar,
  Title,
  User,
  Card,
  StyledCard,
  CardCategory,
};
