         }fetchData();
       }, [token]);



    const logout = () => {
      setToken("");
      window.localStorage.removeItem("token");
    };
  return (
    <div>
      
      
        {!token ? 
        <a
          href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}
        className='login'>
          Login to Spotify
        </a> : <Title onClick={logout}>Logout</Title>
        }
     
    </div>
  );
}

export default Login
