import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Title } from '../Styled';
import { getCurrentUserProfile } from "../../spotify.js";

function Login() {
    const CLIENT_ID = "f32d7d685abd4624a20f043150dee8fa";
    const REDIRECT_URI = "http://localhost:3000";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
    const RESPONSE_TYPE = "token";

    const [ token , setToken] = useState('');
    const [profile, setProfile] = useState("");

    useEffect(() => {
      const hash = window.location.hash;
      let token = window.localStorage.getItem("token");

      if (!token && hash) {
        token = hash
          .substring(1)
          .split("&")
          .find((elem) => elem.startsWith("access_token"))
          .split("=")[1];

        window.location.hash = "";
        window.localStorage.setItem("token", token);
      }
      setToken(token);

      if (token != '') {
        const fetchData = async () => {
          console.log(token, "qUERY cALL");
          try {
            const { data } = axios.get("/me");
            setProfile(data);
            console.log(data, "profile");
          } catch (e) {
            console.log(3);

            console.error(e);
          }
        };
        fetchData();
      }
      console.log(1, token);
    }, [token]);

     

      //  useEffect(() => {
      //         console.log(2);
       
      //     console.log(token, "qUERY cALL23");

         
      //  }, [token]);



    const logout = () => {
      setToken("");
      window.localStorage.removeItem("token");
    };
  return (
    <div>
      
      
        {!token ? 
        <a
          href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}
        className='login'>
          Login to Spotify
        </a> : <Title onClick={logout}>Logout</Title>
        }
     
    </div>
  );
}

export default Login
